mapper.insert:
        INSERT INTO Nutrient
	    (
	      DESC_KOR
	      , SERVING_WT
	      , NUTR_CONT1
	      , NUTR_CONT2
	      , NUTR_CONT3
	      , NUTR_CONT4
	      , ANIMAL_PLANT
	    )
	    VALUES
	    
	    
	    (
	 
	        #{item.DESC_KOR}
	        , #{item.SERVING_WT}
	        , #{item.NUTR_CONT1}
	        , #{item.NUTR_CONT2}
	        , #{item.NUTR_CONT3}
	        , #{item.NUTR_CONT4}
	        , #{item.ANIMAL_PLANT}
	        )

mapper.update:
    UPDATE Nutrient
    
		SET 
		
	SERVING_WT=#{SERVING_WT}, NUTR_CONT1=#{NUTR_CONT1}, 
	NUTR_CONT2=#{NUTR_CONT2}, NUTR_CONT3=#{NUTR_CONT3}, 
	NUTR_CONT4=#{NUTR_CONT4}
	WHERE IDX_NU=#{IDX_NU}
